mapper.select:
select dg.map_id map_id,
dg.id data_group_id, dg.name data_group_name,
dgf.id field_id, dgf.name field_name, dt.type field_type from data_group dg, data_group_field dgf, map m, data_type dt where dg.id = dgf.data_group_id and m.id = dg.map_id and m.owner = #{userId} and dt.id = dgf.data_type_id order by dgf.seq;

mapper.update:
update data_group
set name = #{dataGroupName}
where id = #{dataGroupId};
